function chamarAPIFrio() {
  fetch('http://localhost:5000/api/temperatura/frio')
    .then(response => {
      if (!response.ok) throw new Error('Erro na resposta da API');
      return response.json();
    })
    .then(data => {
      alert(`Local: ${data.local}\nPaís: ${data.pais}\nTemperatura: ${data.temperatura} graus\nHorário: ${data.horario}`);
    })
    .catch(err => alert('Erro ao chamar API: ' + err.message));
}

