import React, { useState } from 'react';

const API_BASE_URL = 'http://localhost:5000';

function App() {
  const [dados, setDados] = useState(null);

  const buscarFrio = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/api/temperatura/frio`);
      const data = await res.json();
      setDados(data); // Atualiza o mesmo estado com dadosFrio
    } catch {
      alert('Erro ao buscar dados frio');
    }
  };

  const buscarQuente = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/api/temperatura/quente`);
      const data = await res.json();
      setDados(data); // Atualiza o mesmo estado com dadosQuente
    } catch {
      alert('Erro ao buscar dados quente');
    }
  };

  return (
    <div
      style={{
        padding: '1rem',
        fontFamily: 'Arial',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        boxSizing: 'border-box',
      }}
    >
      <h1>Temperatura</h1>

      <div>
        <button onClick={buscarFrio} style={{ marginRight: '1rem' }}>
          Frio
        </button>
        <button onClick={buscarQuente}>Quente</button>
      </div>

      {dados && (
        <div
          style={{
            marginTop: '2rem',
            border: '1px solid #ddd',
            padding: '1rem',
            borderRadius: '5px',
            width: '300px',
            textAlign: 'center',
            boxShadow: '0 0 10px rgba(0,0,0,0.1)',
          }}
        >
          <h3>Dados de Temperatura</h3>
          <p><strong>Local:</strong> {dados.local}</p>
          <p><strong>País:</strong> {dados.pais}</p>
          <p><strong>Temperatura:</strong> {dados.temperatura} °C</p>
          <p><strong>Horário:</strong> {dados.horario}</p>
        </div>
      )}
    </div>
  );
}

export default App;
