import React, { useState } from 'react';

function App() {
  const [dados, setDados] = useState(null);

  const buscarDados = async (tipo) => {
    try {
      const res = await fetch(`http://localhost:5000/api/temperatura/${tipo}`);
      const data = await res.json();
      setDados(data);
    } catch (error) {
      alert('Erro ao buscar dados');
      setDados(null);
    }
  };

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial' }}>
      <h1>Temperatura</h1>
      <button onClick={() => buscarDados('frio')} style={{ marginRight: '1rem' }}>
        Frio
      </button>
      <button onClick={() => buscarDados('quente')}>
        Quente
      </button>

      {dados && (
        <div style={{ marginTop: '2rem', border: '1px solid #ddd', padding: '1rem', borderRadius: '5px' }}>
          <p><strong>Local:</strong> {dados.local}</p>
          <p><strong>País:</strong> {dados.pais}</p>
          <p><strong>Temperatura:</strong> {dados.temperatura} °C</p>
          <p><strong>Horário:</strong> {dados.horario}</p>
        </div>
      )}
    </div>
  );
}

export default App;
