import React, { useState } from 'react';

function App() {
  const [dados, setDados] = useState(null);
  const [contador, setContador] = useState(null); // estado para a contagem

  const buscarDados = async (tipo) => {
    try {
      const res = await fetch(`http://localhost:5000/api/temperatura/${tipo}`);
      const data = await res.json();
      setDados(data);
    } catch (error) {
      alert('Erro ao buscar dados');
      setDados(null);
    }
  };

  const buscarContador = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/temperatura/contador');
      const data = await res.json();
      setContador(data);
    } catch (error) {
      alert('Erro ao buscar contagem');
      setContador(null);
    }
  };

  // Buscar a contagem automaticamente ao carregar o componente (opcional)
  React.useEffect(() => {
    buscarContador();
  }, []);

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial' }}>
      <h1>Temperatura</h1>

      <button onClick={() => buscarDados('frio')} style={{ marginRight: '1rem' }}>
        Frio
      </button>
      <button onClick={() => buscarDados('quente')}>
        Quente
      </button>

      {/* Caixa para mostrar o contador */}
      {contador && (
        <div style={{ marginTop: '1.5rem', padding: '1rem', border: '1px solid #888', borderRadius: '5px', backgroundColor: '#eee' }}>
          <h3>Contagem de seleções:</h3>
          {contador.map(({ tipo, contagem }) => (
            <p key={tipo}><strong>{tipo}:</strong> {contagem}</p>
          ))}
        </div>
      )}

      {dados && (
        <div style={{ marginTop: '2rem', border: '1px solid #ddd', padding: '1rem', borderRadius: '5px' }}>
          <p><strong>Local:</strong> {dados.local}</p>
          <p><strong>País:</strong> {dados.pais}</p>
          <p><strong>Temperatura:</strong> {dados.temperatura} °C</p>
          <p><strong>Horário:</strong> {dados.horario}</p>
        </div>
      )}
    </div>
  );
}

export default App;
