import React, { useState } from 'react';

function Temperatura() {
  const temperaturas = [10, 14, 18, 22, 25, 30, 35, 40];
  const [temperaturaSelecionada, setTemperaturaSelecionada] = useState(null);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Escolha a temperatura</h1>
      <div>
        {temperaturas.map((temp) => (
          <button
            key={temp}
            onClick={() => setTemperaturaSelecionada(temp)}
            style={{
              margin: '5px',
              padding: '10px 15px',
              cursor: 'pointer',
              backgroundColor: temperaturaSelecionada === temp ? '#4CAF50' : '#e7e7e7',
              color: temperaturaSelecionada === temp ? 'white' : 'black',
              border: 'none',
              borderRadius: '4px',
            }}
          >
            {temp} Â°C
          </button>
        ))}
      </div>
      {temperaturaSelecionada !== null && (
        <p>Temperatura selecionada: {temperaturaSelecionada} graus</p>
      )}
    </div>
  );
}

export default Temperatura;
