import React, { useState } from 'react';

function App() {
  const [dados, setDados] = useState(null);

  const buscarTemperatura = async (tipo) => {
    try {
      const response = await fetch(`http://localhost:5000/api/temperatura/${tipo}`);
      const data = await response.json();
      setDados(data);
    } catch (error) {
      console.error('Erro ao buscar dados:', error);
    }
  };

  return (
    <div>
      <h1>Temperatura</h1>
      <button onClick={() => buscarTemperatura('frio')}>Frio</button>
      <button onClick={() => buscarTemperatura('quente')}>Quente</button>
      {dados && (
        <div>
          <p><strong>Local:</strong> {dados.local}</p>
          <p><strong>País:</strong> {dados.pais}</p>
          <p><strong>Temperatura:</strong> {dados.temperatura}°C</p>
          <p><strong>Horário:</strong> {dados.horario}</p>
        </div>
      )}
    </div>
  );
}

export default App;
