import React from 'react';
import './style.css'; // exemplo de importação CSS

function App() {
  function chamarAPIFrio() {
    fetch('http://localhost:5001/api/temperatura/frio')
      .then(res => res.json())
      .then(data => alert(`Frio: ${data.temperatura} graus em ${data.local}`));
  }

  function chamarAPIQuente() {
    fetch('http://localhost:5001/api/temperatura/quente')
      .then(res => res.json())
      .then(data => alert(`Quente: ${data.temperatura} graus em ${data.local}`));
  }

  return (
    <div>
      <button onClick={chamarAPIFrio}>Temperatura Fria</button>
      <button onClick={chamarAPIQuente}>Temperatura Quente</button>
    </div>
  );
}

export default App;
